# Load image packagepkg load imageclose all  % figures windowsclc % command windowclear % variables in memory# Reading the image.img = imread("/Users/ASamir/flo.jpg");[img_height img_width channels] = size(img);# Converting to grey scale.img_grey = rgb2gray(img);# Applying noiseimg_noise = imnoise(img_grey,'salt & pepper', 0.05);# Display.figure();subplot (2,1,1)imshow(img);subplot (2,1,2)imshow(img_noise);# Converting to doubleFOR CAculations.img_grey = double(img_grey);img_noise = double(img_noise);# Filter 1: Average h_avg = ones(3,3) / 9;h_avg# Filter 2: Removing regions color and keep edges only.h_edge = ones(3,3);h_edge(2, 2) = -8;h_edge# Filter 3: Gx the direction of horizontal edges.h_xedge = zeros(3,3);h_xedge(1,1) = 1;h_xedge(1,2) = 1;h_xedge(1,3) = 1;h_xedge(3,1) = -1;h_xedge(3,2) = -1;h_xedge(3,3) = -1;h_xedge# Filter 4: Gy the direction of vertical edges.h_yedge = zeros(3,3);h_yedge(1,1) = 1;h_yedge(2,1) = 1;h_yedge(3,1) = 1;h_yedge(1,3) = -1;h_yedge(2,3) = -1;h_yedge(3,3) = -1;h_yedge# Images after filters.i1 = imfilter(img_noise, h_avg);i2 = imfilter(img_grey, h_edge);i3 = imfilter(img_grey, h_xedge);i4 = imfilter(img_grey, h_yedge);#Conveyt to uint8i1 = uint8(i1);i2 = uint8(i2);i3 = uint8(i3);i4 = uint8(i4);# Displayfigure();subplot (2,2,1);imshow(i1);subplot (2,2,2);imshow(i2);subplot (2,2,3);imshow(i3);subplot (2,2,4);imshow(i4);