# Load image packagepkg load imageclose all  % figures windowsclc % command windowclear % variables in memory# Create a 21x21 image.A = zeros(21, 21);#A(11, 10) = 1; #A(11, 12) = 1;#A(11, 4) = 1; #A(11, 18) = 1;A(4, 10) = 1; A(14, 10) = 1; # ToDo @Samir55 Try to find more about this you have guessed the values upon intuition or ask about it.A = double(A);# Get Forier inverse.A_fft = ifft2(A);A_shift = ifftshift(A_fft);# Post processing.A_abs = abs(A_shift);A_log = log(A_abs +1);# Plot.figure();subplot(1, 2, 1);imshow(A, []);subplot(1, 2, 2);imshow(A_log, []);